INSERT INTO location(location_id,name,zone,tol_describe,description,tel,address,zipcode,region,town,travelling_info,open_time,picture1,pic_describe1,picture2,pic_describe2,picture3,pic_describe3,map,gov,longitude,latitude,orgclass,class1,class2,class3,level,website,parking_info,parking_info_px,parking_info_py,ticket_info,remarks,keyword,change_time) VALUES ('C1_379000000A_001572','西門町',NULL,'西門町之名來自日據時代，當時的居民大多居住於臺北城內，西門外的這塊地方就是他們的主要休憩場所。早期這裡就以電影院為最主要的商業活動，後來陸續有百貨業及其他娛樂場所的進駐。1961年中華商場完工後，西門町還一度是全國最大的商業娛樂中心。▲圖片來源：臺北市政府觀光傳播局西門町有許多著名的地標及商場，像是IG打卡景點的彩虹地景、擁有100多年歷史的西門紅樓、時代性指標的萬年商業大樓、充滿美式風格的西門町美國街等；還有多樣美食聚集在此，如港式料理、台式料理、美式料理、飲料冰品、咖啡廳、酒吧等各式餐點。▲圖片來源：臺北市政府觀光傳播局經過民國80年代的沉寂，西門町在捷運帶動與政府的規劃下，搖身一變成了臺北市第一條具有指標性意義的徒步區。每逢假日這兒總是人潮不斷，把騎樓與街道擠得水洩不通。無論吃的、用的、穿的、玩的，各種流行時尚、新奇有趣的商品，這裡應有盡有，可說是臺北最熱鬧的街道。不但是青少年文化與街頭表演的聚集地，也是青少年盛裝約會的地點。武昌街旁邊的美國街，則充滿了美式風格的嘻哈服飾與個性行頭、塗鴉創作，為臺北畫上了異國的妝扮。▲圖片來源：臺北市政府觀光傳播局現今的西門町不但見證了歷史，還是新一代流行文化的帶領者，許多懷舊的中老年人在此回味往事，更有打扮新潮的年輕一代在這裡爭奇鬥豔，兩種完全不同的生活型態在此交流，滿足了不同需要的族群。',NULL,'886-2-23754205',NULL,'108','臺北市','萬華區',NULL,NULL,'https://www.travel.taipei/image/222890','西門町',NULL,NULL,NULL,NULL,NULL,'379000000A','121.507530','25.044580',NULL,'12',NULL,NULL,'9','https://www.ximen.com.tw/index.html',NULL,NULL,NULL,NULL,NULL,NULL,'2023/09/21 10:36:36');